import React, {useState, useEffect} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import {createSocketConnectionInstance} from './Connection';
import Button from '@material-ui/core/Button';
import { AppBar, Toolbar } from '@material-ui/core';
import Box from '@material-ui/core/Box';
let socketInstance;

const MeetWindow =(props)=>{
  
    const roomId = useState(props.location.pathname.substring(1));
    const [micStatus,setMic]=useState(true);
    const [videoStatus,setCam] =useState(true);
    const [recordStatus,setRecord]=useState(true);

    useEffect(() => {
       
      socketInstance=(createSocketConnectionInstance({
        roomId:roomId,
        micStatus:micStatus,
        videoStatus:videoStatus,
      }))
        
        
    },[])



    const exitcall=()=>{
      socketInstance.exitcall();
      props.history.push('/');
    }



    const mictoggle=()=>{
      socketInstance.mictoggle(micStatus)
      setMic(!micStatus);

    }


    const videotoggle=()=>{
      socketInstance.videotoggle(videoStatus);
      setCam(!videoStatus);
    }
   

    const screenShare=()=>{
      socketInstance.screenSharetoggle();
      
    }

    const recordScreen=()=>{
      socketInstance.recordScreen(recordStatus);
      setRecord(!recordStatus);
    }
    const useStyles = makeStyles({
      root: {
        background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
        border: 0,
        borderRadius: 3,
        boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
        color: 'white',
        height: 48,
        padding: '0 30px',
      },
      

      
    });
  

    
    return(<React.Fragment><h1>hello there </h1> <Box display="flex" id="video-grid"></Box>
     <AppBar className="footbar-wrapper" position="fixed" color="primary">
                <Toolbar className={`footbar-tool ${props.className}`}>
                <Button onClick={mictoggle}>mic</Button><Button onClick={exitcall}>exit</Button><Button onClick={videotoggle}>cam</Button> <Button onClick={screenShare}>screenShare</Button><Button onClick={recordScreen}>Record</Button>
                </Toolbar>
            </AppBar></React.Fragment>)
};

export default MeetWindow;
