import React, {useState, useEffect} from 'react';

import {createSocketConnectionInstance} from './Connection';
import Button from '@material-ui/core/Button';


let socketInstance;

const MeetWindow =(props)=>{
  
    const roomId = useState(props.location.pathname.substring(1));
    const [micStatus,setMic]=useState(false);
    const [videoStatus,setCam] =useState(true);


    useEffect(() => {
       
      socketInstance=(createSocketConnectionInstance({
        roomId:roomId,
        micStatus:micStatus,
        videoStatus:videoStatus,
      }))
        
        
    },[])



    const exitcall=()=>{
      socketInstance.exitcall();
      props.history.push('/');
    }



    const mictoggle=()=>{
      socketInstance.mictoggle(micStatus)
      setMic(!micStatus);

    }


    const videotoggle=()=>{
      socketInstance.videotoggle(videoStatus);
      setCam(!videoStatus);
    }
   
    
    return(<React.Fragment><h1>hello there </h1> <div id="video-grid"></div><Button onClick={mictoggle}>mic</Button><Button onClick={exitcall}>exit</Button><Button onClick={videotoggle}>cam</Button></React.Fragment>)
};

export default MeetWindow;
