import React, {useState, useEffect} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import {createSocketConnectionInstance} from './Connection';
import Button from '@material-ui/core/Button';
import { AppBar, Toolbar } from '@material-ui/core';
import Box from '@material-ui/core/Box';
import './styles.css';
let socketInstance;

const MeetWindow =(props)=>{
  
    const roomId = (props.location.pathname.substring(1));
    const [micStatus,setMic]=useState(true);
    const [videoStatus,setCam] =useState(true);
    const [recordStatus,setRecord]=useState(true);

    useEffect(() => {
       
      socketInstance=(createSocketConnectionInstance({
        roomId:roomId,
        micStatus:micStatus,
        videoStatus:videoStatus,
        recordStatus:recordStatus,
        updatevalue: updatevalue,
      }))
        
        
    },[])

    const updatevalue=(property,val)=>{
      if(property==='recordStatus')
      {
        setRecord(val);
      }
      else if(property==='micStatus')
      {
        setMic(val);
      }
      else if(property==='videpStatus')
      {
        setCam(val);
      }

    }

    const exitcall=()=>{
      socketInstance.exitcall();
      props.history.push('/');
    }



    const mictoggle=()=>{
      socketInstance.mictoggle(micStatus)
      setMic(!micStatus);

    }


    const videotoggle=()=>{
      socketInstance.videotoggle(videoStatus);
      setCam(!videoStatus);
    }
   

    const screenShare=()=>{
      socketInstance.screenSharetoggle();
      
    }

    const recordScreen=()=>{
      socketInstance.recordScreen(recordStatus);
      setRecord(!recordStatus);
    }

    const copyLink=()=>{
      prompt('Copy the code to share with others',roomId);
    }
    const useStyles = makeStyles({
      root: {
        background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
        border: 0,
        borderRadius: 3,
        boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
        color: 'white',
        height: 48,
        padding: '0 30px',
      },
      
      

      
    });
  

    
    return(<React.Fragment><h1>hello there </h1> <Box display="flex" flexWrap="wrap" justify-content="space-evenly"  id="video-grid"></Box>
     </React.Fragment>)
};

export default MeetWindow;
