import React, {useState, useEffect} from 'react';
import io from 'socket.io-client';
import Peer from 'peerjs';
let socket;


const MeetWindow =(props)=>{
    const ENDPOINT='localhost:5000';
    const [roomId, setRoom] = useState(props.location.pathname.substring(1));
    const [userId, setUserId]= useState(null);
    const peer=new Peer();
    

    useEffect(() => {
       
   
      console.log(`connecting to server:${ENDPOINT} `);
      socket = io(ENDPOINT);
      peer.on('open',id =>{
        setUserId(id);
        socket.emit('joining-room', roomId, id);
      })
       

        socket.on('user-joined',(Id)=>{
          console.log("Joined user:"+Id);
        })
        console.log('ID:'+userId);
        return () => {
            socket.emit('disconnet');
            socket.off();
        }
        
       
        
        
    },[ENDPOINT])
   
    return(<h1>hello there {roomId}</h1>)
};

export default MeetWindow;
