import React, {useState} from 'react';
import Axios from 'axios';
import {Link} from 'react-router-dom';
import Button from '@material-ui/core/Button';



const LandingPage= (props)=> {
      const [roomId, setRoom] = useState('');
      const createroom = ()=> {
        Axios.get('http://localhost:5000/',{
          cors: {
            origin: "http://localhost:3000",
            methods: ["GET", "POST"]
          }
        }).then((res) =>{
          setRoom(res.data.link);
          
      });
        };
      
      const handlejoin=()=>{
        if(roomId)
        props.history.push(`/${roomId}`);
      }

      return (<React.Fragment>
      <div align="center">
      <div><Button variant="contained" color="primary"  onClick={createroom}> Create Room</Button></div>
      
      <div><input type="text" name='value' value={roomId} onChange={(e) =>{setRoom(e.target.value) }} /></div>
     
      {roomId}
      
      <div><Button variant="contained" color="primary" onClick={handlejoin} > Join Room</Button></div> 
      
      </div>
      </React.Fragment>
      )
};

export default LandingPage;
