import React, {useState, useEffect} from 'react';
import io from 'socket.io-client';
import Axios from 'axios';
import Peer from 'peerjs';
let socket;

const MeetWindow =(props)=>{
    const ENDPOINT='localhost:5000';
    const [roomId, setRoom] = useState('');
    const [userId, setUserId]= useState('');
    const peer=new Peer();
    const AssignRoomId =()=>{
      setRoom(props.location.pathname.substring(1));
    }
    const AssignUserId =()=>{
      peer.on('open',id =>{
        setUserId(id);
      })
    }
    useEffect(() => {
        //need to change here
       AssignRoomId();
       AssignUserId();
      
        if(roomId!=='' && userId!=='')
        {
        console.log(`connecting to server:${ENDPOINT}`);
        socket = io(ENDPOINT);
        socket.emit('joining-room', roomId, userId);

        socket.on('user-joined',(Id)=>{
          console.log("connected user:"+Id);
        })
        
        return () => {
            socket.emit('disconnet');
            socket.off();
        }
        }
        console.log('inside meet window');
        console.log({roomId});
        
    },[ENDPOINT])
   
    return(<h1>hello there {roomId}</h1>)
};

export default MeetWindow;
