import React, {useState} from 'react';
import Axios from 'axios';
import {Link} from 'react-router-dom';
import Button from '@material-ui/core/Button';



const LandingPage= ()=> {
      const [roomId, setRoom] = useState('');
      const createroom = ()=> {
        Axios.get('http://localhost:5000/',{
          cors: {
            origin: "http://localhost:3000",
            methods: ["GET", "POST"]
          }
        }).then((res) =>{
          setRoom(res.data.link);
          
      });
        };

      return (<React.Fragment>
   
      <div><Button variant="contained" color="primary" align="center" onClick={createroom}> Create Room</Button></div>
      
      <div><input type="text" name='value' value={roomId} onChange={(e) =>{setRoom(e.target.value) }} /></div>
     
      {roomId}
      <Link onClick={event => (!roomId) ? event.preventDefault(): null} to={{ pathname: `/${roomId}`,
      roomdetails: {room:{roomId} } }}>
      <div><button onClick={()=>{console.log('Join room clicked')}}> Join Room</button></div> 
      </Link>
      </React.Fragment>
      )
};

export default LandingPage;
