    Tracks.forEach(element => {
      if (element.kind === 'video') {
        if (videostatus) element.stop();
      }
    });
    if (!videostatus) this.enabletrack(videostatus, micStatus);
  }

  mictoggle(videostatus, micStatus) {
    const Tracks = this.videoContainer[this.userId].getTracks();
    Tracks.forEach(element => {
      if (element.kind === 'audio') {
        if (micStatus) element.stop();
      }
    });
