import React, {useState, useEffect} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import {createSocketConnectionInstance} from './Connection';
import Button from '@material-ui/core/Button';
import { AppBar, Toolbar } from '@material-ui/core';
import MicIcon from '@material-ui/icons/Mic';
import MicOffIcon from '@material-ui/icons/MicOff';
import VideocamIcon from '@material-ui/icons/Videocam';
import VideocamOffIcon from '@material-ui/icons/VideocamOff';
import CallEndRoundedIcon from '@material-ui/icons/CallEndRounded';
import IconButton from '@material-ui/core/IconButton';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import MoreVertIcon from '@material-ui/icons/MoreVert';

import Box from '@material-ui/core/Box';

import './styles.css';
let socketInstance;

const MeetWindow =(props)=>{
  
    const roomId = (props.location.pathname.substring(1));
    const [micStatus,setMic]=useState(true);
    const [videoStatus,setCam] =useState(true);
    const [recordStatus,setRecord]=useState(true);
    const [screenShareStatus,setScreen]=useState(true);
    useEffect(() => {
       
      socketInstance=(createSocketConnectionInstance({
        roomId:roomId,
        micStatus:micStatus,
        videoStatus:videoStatus,
        recordStatus:recordStatus,
        updatevalue: updatevalue,
      }))
        
        
    },[])

    const updatevalue=(property,val)=>{
      if(property==='recordStatus')
      {
        setRecord(val);
      }
      else if(property==='micStatus')
      {
        setMic(val);
      }
      else if(property==='videoStatus')
      {
        setCam(val);
      }

    }

    const exitcall=()=>{
      socketInstance.exitcall();
      props.history.push('/');
    }



    const mictoggle=()=>{
      socketInstance.mictoggle(micStatus)
      setMic(!micStatus);

    }


    const videotoggle=()=>{
      socketInstance.videotoggle(videoStatus);
      setCam(!videoStatus);
    }
   

    const screenShare=()=>{
      socketInstance.screenSharetoggle();
      
    }

    const recordScreen=()=>{
      socketInstance.recordScreen(recordStatus);
      setRecord(!recordStatus);
    }

    const copyLink=()=>{
      prompt('Copy the code to share with others',roomId);
    }
    
    const useStyles = makeStyles({
      root: {
        background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
        border: 0,
        borderRadius: 3,
        boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
        color: 'white',
        height: 48,
        padding: '0 30px',
      },
      
      

      
    });

    const handleClick = (event) => {
      setAnchorEl(event.currentTarget);
    };
  
    const handleClose = () => {
      setAnchorEl(null);
    };
  

    
    return(<React.Fragment><div id="video-grid"> <video id="screen-share" display="none"/> </div>
     <AppBar className="footbar-wrapper" position="bottom" color="primary" >
                <Toolbar className={`footbar-tool`}>
                <div  onClick={mictoggle} title={micStatus ? 'Disable Mic' : 'Enable Mic'}>
                        {micStatus ?<MicIcon/>:<MicOffIcon/>}
                </div>
                <div  onClick={exitcall} >
                        <CallEndRoundedIcon/>
                </div>
              
               <div  onClick={videotoggle} title={micStatus ? 'Disable Cam' : 'Enable Cam'}>
                        {videoStatus ?<VideocamIcon/>:<VideocamOffIcon/>}
                </div>  
                <IconButton
                    aria-label="more"
                    aria-controls="long-menu"
                    aria-haspopup="true"
                    onClick={handleClick}>
                      <MoreVertIcon />
                </IconButton>
                <Menu
                  id="more"
                  anchorEl={anchorEl}
                  keepMounted
                  open={open}
                  onClose={handleClose}
                  TransitionComponent={Fade}>
        <MenuItem onClick={handleClose}>Profile</MenuItem>
        <MenuItem onClick={handleClose}>My account</MenuItem>
        <MenuItem onClick={handleClose}>Logout</MenuItem>
      </Menu>
                </Toolbar>

                <Button onClick={screenShare}>screenShare</Button><Button onClick={recordScreen}>Record</Button> <Button onClick={copyLink}>add user</Button>
            </AppBar></React.Fragment>)
};

export default MeetWindow;

